import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function WeatherApp() {
  const [data, setData] = useState({});
  const [location, setLocation] = useState('');
  const [loading, setLoading] = useState(false);

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=cc573655e6857b05ad3c7a1b72d38585`;

  const searchLocation = (event) => {
    if (event.key === 'Enter' && location.trim() !== '') {
      setLoading(true);
      axios
        .get(url)
        .then((response) => {
          setData(response.data);
          console.log(response.data);
        })
        .catch((error) => {
          console.error('Error fetching data:', error);
          alert('Location not found. Please enter a valid location.');
        })
        .finally(() => {
          setLoading(false);
        });
      setLocation('');
    }
  };

  return (
    <div className="app">
      <div className="search">
        <input
          value={location}
          onChange={(event) => setLocation(event.target.value)}
          onKeyPress={searchLocation}
          placeholder="Enter Location"
          type="text"
        />
      </div>
      <div className="container">
        {loading ? (
          <p>Loading...</p>
        ) : (
          <>
            <div className="top">
              <div className="location">
                <p>{data.name}</p>
              </div>
              <div className="temp">
                {data.main ? <h1>{(data.main.temp - 273.15).toFixed()}°C</h1> : null}
                {data.main ? <h1>{((data.main.temp - 273.15) * 9 / 5 + 32).toFixed()}°F</h1> : null}
              </div>
              <div className="description">
                {data.weather ? <p>{data.weather[0].main}</p> : null}
              </div>
            </div>

            {data.name !== undefined && (
              <div className="bottom">
                <div className="feels">
                  {data.main ? <p className="bold">{(data.main.feels_like - 273.15).toFixed()}°C</p> : null}
                  {data.main ? <p className="bold">{((data.main.feels_like - 273.15) * 9 / 5 + 32).toFixed()}°F</p> : null}
                  <p>Feels Like</p>
                </div>
                <div className="humidity">
                  {data.main ? <p className="bold">{data.main.humidity}%</p> : null}
                  <p>Humidity</p>
                </div>
                <div className="wind">
                  {data.wind ? <p className="bold">{data.wind.speed.toFixed()} MPH</p> : null}
                  <p>Wind Speed</p>
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default WeatherApp;
